import { Component } from '@angular/core';

@Component({
  selector: 'product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  name = 'Jhon Doe';
  addToCart: number = 0;
  product = {
    name: 'Sony Xperia Pro',
    price: 800,
    color: 'black',
    discount: 7.6,
    inStock: 5,
    productUrl:
      'https://sony.scene7.com/is/image/sonyglobalsolutions/00_00_06_Intro_D-1?$productIntroPlatedesktop$&fmt=png-alpha',
  };
  getDiscountedPrice() {
    let discountPercent = (this.product.price * this.product.discount) / 100;
    return this.product.price - discountPercent;
  }
  onChangeEvent(val: any) {
    this.name = val.target.value;
  }
  decrementCartValue() {
    if (this.addToCart > 0) this.addToCart--;
  }
  incrementCartValue() {
    if (this.addToCart <= this.product.inStock) this.addToCart++;
  }
}
<div class="e-kart--product-container">
  <div class="imgContainer">
    <img [src]="product.productUrl" alt="" />
  </div>
  <p>Name: {{ product.name }}</p>
  <p>Price: {{ "$" + product.price }}</p>
  <p>Color: {{ product.color }}</p>
  <p>Discounted Price: {{ "$" + getDiscountedPrice() }}</p>
  <p>
    {{
      product.inStock > 0
        ? "Only " + product.inStock + " item left"
        : "Not in stock"
    }}
  </p>
  <p class="cart">Add items:</p>
  <button [disabled]="!(product.inStock > 0)" (click)="decrementCartValue()">
    -
  </button>
  <span>{{ addToCart }}</span>
  <button [disabled]="!(product.inStock > 0)" (click)="incrementCartValue()">
    +
  </button>
  <!-- <input type="text" [value]="name" (input)="onChangeEvent($event)" />
  <p>Name: {{ name }}</p> -->
</div>
