import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css'],
})
export class SearchComponent {
  searchText: string = '';

  // 1 Create an event
  @Output()
  searchTextChanged: EventEmitter<string> = new EventEmitter<string>();

  onSearchTextChanged() {
    this.searchTextChanged.emit(this.searchText);
  }

  updateSearchText(val: any) {
    console.log(val);
    this.searchText = val.target.value;
  }
}
<div class="ekart--search-product">
  <!-- <input
    type="text"
    class="ekart--search-product-input"
    [value]="searchText"
    (input)="updateSearchText($event)"
  /> -->
  <input
    type="text"
    class="ekart--search-product-input"
    [(ngModel)]="searchText"
    (change)="onSearchTextChanged()"
  />
  <button
    [ngClass]="{
      btn: true,
      'btn-search': searchText
    }"
    [disabled]="!searchText"
  >
    Search
  </button>
</div>
<div class="ekart-search-result-for">
  <p *ngIf="searchText != ''">
    <strong>Search result for: </strong>{{ searchText }}
  </p>
</div>
