import { Component } from '@angular/core';

@Component({
  selector: 'product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  product = {
    name: 'Sony Xperia Pro',
    price: 800,
    color: 'black',
    discount: 7.6,
    inStock: 10,
    productUrl:
      'https://sony.scene7.com/is/image/sonyglobalsolutions/00_00_06_Intro_D-1?$productIntroPlatedesktop$&fmt=png-alpha',
  };
  getDiscountedPrice() {
    let discountPercent = (this.product.price * this.product.discount) / 100;
    return this.product.price - discountPercent;
  }
}
<div class="imgContainer">
  <!-- <img [src]="product.productUrl" alt="" /> -->
</div>
<p>Name: {{ product.name }}</p>
<p>Price: {{ "$" + product.price }}</p>
<p>Color: {{ product.color }}</p>
<p>Discounted Price: {{ "$" + getDiscountedPrice() }}</p>
<p>
  {{
    product.inStock > 0
      ? "Only " + product.inStock + " item is in stock"
      : "Not in stock"
  }}
</p>
