import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.css'],
})
export class FilterComponent {
  @Input()
  all: number = 0;
  @Input()
  inStock: number = 0;
  @Input()
  outOfStock: number = 0;
}
<div class="filter-container">
  <span> Filter: </span>
  <input type="radio" name="filter" id="All" aria-selected="true" />
  <span>{{ "All (" + all + ")" }}</span>

  <input type="radio" name="filter" id="true" />
  <span>{{ "In Stock (" + inStock + ")" }}</span>

  <input type="radio" name="filter" id="false" />
  <span>{{ "Out of Stock (" + outOfStock + ")" }}</span>
</div>
