import { Component, ViewChild } from '@angular/core';
import { ProductListComponent } from './product-list/product-list.component';

@Component({
  selector: 'app-container',
  templateUrl: './container.component.html',
  styleUrls: ['./container.component.css'],
})
export class ContainerComponent {
  // listOfString: string[] = ['Mark', 'Jhon', 'Carl', 'Tony', 'Peter'];

  searchText: string = '';

  @ViewChild(ProductListComponent) productListComponent: ProductListComponent;

  setSearchText(data: string) {
    // console.log(data);
    this.searchText = data;
  }
}
<div class="e-kart--product-container">
  <app-search (searchTextChanged)="setSearchText($event)"></app-search>
  <!-- <div *ngFor="let i of listOfString">
    <p>{{ "My name is " + i }}</p>
  </div> -->
  <product-list [searchText]="searchText" #productListComponent></product-list>
  <product-detail
    *ngIf="productListComponent.selectedProducts"
    [productListComp]="productListComponent"
  ></product-detail>
</div>
